import React from 'react';

export default function Book(props) {
    const { book_image, rating, title, author, publisher} = props.details;
    const { selected, selectAction, index } = props;
    return (
        <div className={`book_wrapper ${selected == index ? 'selected' : null}`} style={{ backgroundImage: `url(${book_image})` }} onClick={() => { selectAction(index) }}>
            <span className="book_rating">{rating}</span>
            <div>
                <cite className="book_title">{title}</cite>
                <p className="book_author">{author}</p>
                <p className="book_publisher">{publisher}</p>
            </div>
        </div>
    )
}
