import React, { Component } from 'react';
import axios from 'axios';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';

class Content extends Component {
    constructor(props) {
        super(props);
        this.state = {
            books:[]
        }
    }

    static getDerrivedStatesFromProps() {

    }

    componentDidMount() {
        axios({
            method:'GET',
            url: '../books.json',
        }).then(response => {
            this.setState({
                books:response.data.results.books
            })
        })
    }

    render() {
        return (
            <>
                {/* <BookCarousel />
                <BookInfo /> */}
                <Carousel
                    additionalTransfrom={0}
                    arrows
                    autoPlaySpeed={3000}
                    centerMode={false}
                    className=""
                    containerClass="container-with-dots"
                    dotListClass=""
                    draggable
                    focusOnSelect={false}
                    infinite
                    itemClass=""
                    keyBoardControl
                    minimumTouchDrag={80}
                    renderButtonGroupOutside={false}
                    renderDotsOutside={false}
                    responsive={{
                        desktop: {
                        breakpoint: {
                            max: 3000,
                            min: 1024
                        },
                        items: 3,
                        partialVisibilityGutter: 40
                        },
                        mobile: {
                        breakpoint: {
                            max: 464,
                            min: 0
                        },
                        items: 1,
                        partialVisibilityGutter: 30
                        },
                        tablet: {
                        breakpoint: {
                            max: 1024,
                            min: 464
                        },
                        items: 2,
                        partialVisibilityGutter: 30
                        }
                    }}
                    showDots={false}
                    sliderClass=""
                    slidesToSlide={1}
                    swipeable
                    >

{this.state.books.map(item => <p>{item.publisher}</p>)}
                    </Carousel>
            </>
        )
    }
}

export default Content;
